svg.script:

var svgns="http://www.w3.org/2000/svg";
var xlinkns="http://www.w3.org/1999/xlink";

// get the current time
var hour, min, sec, centerX, centerY, totWidth, totHeight;

function init(){
	now = new Date();
	hour = now.getHours();
	min = now.getMinutes();
	sec = now.getSeconds();
	console.log("hour: "+hour+" | min: "+min+" | sec: "+sec);

	var clockFaceBox = document.getElementById('clockFace').getBBox();

	totWidth = Math.ceil(clockFaceBox.width);
	totHeight = Math.ceil(clockFaceBox.height); 

	// get the center point
	centerX = Math.ceil(clockFaceBox.width/2);
	centerY = Math.ceil(clockFaceBox.height/2);
	console.log("centerX: "+centerX+" | centerY: "+centerY);

	addSecHand(0,0,0);


}

function addSecHand(x, y, width){
	var start = (hour*30)+(min/2); //sec*6;

	// create the second hand
	var secHand = document.createElementNS(null, "rect");
	secHand.setAttributeNS(null, "x", centerX+"px");
	secHand.setAttributeNS(null, "y", centerY+"px");
	secHand.setAttributeNS(null, "width", (totWidth/100)*1+"px");
	secHand.setAttributeNS(null, "height", (totHeight/100)*20+"px");
	secHand.setAttributeNS(null, "fill", "red");

	document.getElementsByTagName('svg')[0].appendChild(secHand);
}

function animateHand(start){
	var aniElem = document.getElementById('rotate');
	// remove the id
	aniElem.removeAttributeNS(null, "id");
	// add from to and duration

	console.log(start);
	aniElem.setAttributeNS(null, "from", start+","+centerX+","+centerY);
	aniElem.setAttributeNS(null, "to", (start+360)+","+centerX+","+centerY);
	aniElem.setAttributeNS(null, "dur", "60s");
	// attach to the second hand
	console.log(secHand);
}

