svg.script:

Root=document.documentElement
xmlns="http://www.w3.org/2000/svg"
Colors=new Array("magenta","green","cyan","white","purple","orange")
function start(){
	var container=document.getElementById("container").getBBox()
	var centerX=container.width/2
	var centerY=container.height/2
	var center=centerX+","+centerY
	var rotate60s=document.getElementById("rotate60s")
	var rotateTransform=document.getElementById("rotateTransform")
	var clockFace=document.getElementById("clockFace")
	var gears=document.getElementById("gears")
	var now=new Date()
	hour=now.getHours()
	minute=now.getMinutes()
	second=now.getSeconds()
	var timeDurations=new Array(hour*30+minute/2,minute*6+second/10,second*6)
	var Times=new Array(43200,3600,60)
	rects=document.getElementsByTagName("rects")
	values="360,"+centerX+","+centerY+";0,"+centerX+","+centerY
	rotateTransform.setAttributeNS(null,"values" ,values )

	// for each of the rects
	for (i=1;i<rects.length;i++){
		// clone the basic rotate animate
		var CL=rotate60s.cloneNode("false")
		CL.setAttributeNS(null, "from", timeDurations[i-1]+","+center)
		CL.setAttributeNS(null, "to", (timeDurations[i-1]+360)+","+center)
		CL.setAttributeNS(null, "dur", Times[i-1]+"second")
		rects.item(i).appendChild(CL)
	}
}
//
